<?php/*Plugin Name: 	plugin auth WPSDescription: 	MU Plugin pour mettre en évidence les plugins interdits/recommandés. Sert aussi à mettre en évidence certaines options non recommandées de W3 Total Cache. Ajoute un menu WPServeur et met en avant le bouton pour purger Varnish dans la barre de menu Admin.Version: 		1.2.01032016Author: 		Benoît de WP ServeurAuthor URI: 	https://www.wpserveur.netDate: 			01/03/2016*//** Pour le style du bouton varish et le menu wpserveur */function wps_enqueue_style_script (){	echo '		<style type="text/css" media="screen">		/*		 *regles pour le bouton purge varnish		 */			li#wp-admin-bar-purge-varnish-cache-all a {				background: #88B92A;				}				li#wp-admin-bar-purge-varnish-cache-all a:hover {					background: #f1f1f1 !important;					}			li#wp-admin-bar-purge-varnish-cache-all a:before {				content: "\f531";				margin-top: 2px;				}			/*			 *regles pour le bouton menu WPS			 */			#wp-admin-bar-link-wps>.ab-item:before {				content: "\f115";				margin-top: 2px;				}		</style>	';}add_action( 'wp_enqueue_scripts', 'wps_enqueue_style_script' );/** INFORMATIONS PLUGIN INTERDIT ET CONSEILLE *//*ajout du fichier style-admin-wps.css pour les plugins interdit*/	function plugin_wps_enqueue_scripts(){	$mu_plugins_path = content_url('mu-plugins'); //retourne le dossier mu-plugins		echo '		<link rel="stylesheet" href="'.$mu_plugins_path.'/style-admin-wps.css" type="text/css" media="all">		';	}	add_action( 'admin_enqueue_scripts', 'plugin_wps_enqueue_scripts' );